function scale(x, fromLow, fromHigh, toLow, toHigh) {
  return (x - fromLow) * (toHigh - toLow) /
    (fromHigh - fromLow) + toLow;
}


console.log(scale(1, 0, 180, 544, 2400));
console.log(scale(2, 0, 180, 544, 2400));

console.log(scale(1.001, 0, 180, 544, 2400));
console.log(scale(1.002, 0, 180, 544, 2400));
